<span style="color:#8b008b;font-weight:bold">color</span>:<span style="color:#b452cd">#FFFFFF</span>;
}
#<span style="color:#008b45;text-decoration:underline">below</span>{
<span style="color:#8b008b;font-weight:bold">width</span>:<span style="color:#b452cd">100</span><span style="color:#00688b;font-weight:bold">%</span>;
<span style="color:#8b008b;font-weight:bold">height</span>:<span style="color:#b452cd">100</span><span style="color:#00688b;font-weight:bold">px</span>;
<span style="color:#8b008b;font-weight:bold">background-color</span>:<span style="color:#b452cd">#757575</span>;
<span style="color:#8b008b;font-weight:bold">z-index</span>: <span style="color:#b452cd">-1</span>;
}
&lt;/<span style="color:#8b008b;font-weight:bold">style</span>&gt;
&lt;<span style="color:#8b008b;font-weight:bold">script</span> <span style="color:#658b00">type</span>=<span style="color:#cd5555">&#34;text/javascript&#34;</span>&gt;
<span style="color:#8b008b;font-weight:bold">function</span> rollbackTop(){
<span style="color:#658b00">window</span>.scrollTo(<span style="color:#b452cd">0</span>);
}
&lt;/<span style="color:#8b008b;font-weight:bold">script</span>&gt;
&lt;/<span style="color:#8b008b;font-weight:bold">head</span>&gt;
&lt;<span style="color:#8b008b;font-weight:bold">body</span>&gt;
